// Input: GetMemory2
const GetMemory2 = $item("0").$node["Mapping"].json["GetMemory2"];

// Check if GetMemory2 is an array; if not, convert it into an array
const memoryArray = Array.isArray(GetMemory2) ? GetMemory2 : JSON.parse(GetMemory2);

// Transform the array into a single string joined by a space
const transformedString = memoryArray.join(" ");

// Return the result as a JSON object with transformedValue as a string
return [{ json: { transformedValue: transformedString } }];

