// Extract message from the "Clean User's Message" node output
const message = $node["Clean User's Message"].json["transformedValue"];

// Define a regex pattern to detect a phone number (8 to 11 digits)
const phoneRegex = /\b\d{8,11}\b/; // \b ensures whole numbers are detected properly

// Search for a phone number in the message
const match = message.match(phoneRegex);

// Return the extracted phone number or null if not found, using the expected key
return match ? [{ SystemCustomerPhoneNumber: match[0] }] : [{ SystemCustomerPhoneNumber: null }];
